import { ComputedRef, Ref } from 'vue';
export type IFilesViewId = 'files' | 'favorites' | 'recent';
export interface IFilesView {
    /**
     * Unique ID of the view
     */
    id: IFilesViewId;
    /**
     * Localized name of the view
     */
    label: string;
    /**
     * The SVG icon paths
     */
    icon: string;
}
/**
 * Get available file views
 * @param isAnonymous - Whether the user is anonymous
 */
export declare const useViews: (isAnonymous: Ref<boolean> | ComputedRef<boolean>) => {
    allViews: IFilesView[];
    availableViews: IFilesView[];
};
